import{_ as e,c as a,a2 as n,o as r}from"./chunks/framework.DL40nPW_.js";const l="/HIGVR/assets/AboutGlove.DVhgVC7s.png",o="/HIGVR/assets/NGMain.lbydyxES.png",i="/HIGVR/assets/AdjustSensor.B56nW4L7.png",s="/HIGVR/assets/1.BOpIBKB9.jpg",d="/HIGVR/assets/2.ClNIq1GX.jpg",c="/HIGVR/assets/3.B2zMFXou.jpg",g="/HIGVR/assets/4.DyB1fGfp.jpg",u="/HIGVR/assets/1.C77VZMzk.jpg",h="/HIGVR/assets/2.YETY8G5j.jpg",p="/HIGVR/assets/3.8LRJ6E_U.jpg",m="/HIGVR/assets/4.THKT4LKb.jpg",y="/HIGVR/assets/5.D_Qgtn0q.jpg",b="/HIGVR/assets/6.B52rBfln.jpg",x="/HIGVR/assets/7.DNkYytS4.jpg",q=JSON.parse('{"title":"Instruction for HIG Glove","description":"","frontmatter":{"prev":false,"next":{"text":"Quick Start","link":"HIGGlove/2QuickStart"}},"headers":[],"relativePath":"HIGGlove/1BasicPreparation.md","filePath":"HIGGlove/1BasicPreparation.md","lastUpdated":null}'),f={name:"HIGGlove/1BasicPreparation.md"};function G(v,t,I,k,H,w){return r(),a("div",null,t[0]||(t[0]=[n('<h1 id="instruction-for-hig-glove" tabindex="-1">Instruction for HIG Glove <a class="header-anchor" href="#instruction-for-hig-glove" aria-label="Permalink to &quot;Instruction for HIG Glove&quot;">​</a></h1><p>Thank you for choosing and purchasing our HIG Glove product. Below is an instructional document for this product that we hope will help you.</p><p>You can also join our <a href="https://discord.gg/RqhmCevwv3" target="_blank" rel="noreferrer">Discord</a> to find Help.</p><h1 id="basic-preparation" tabindex="-1">Basic Preparation <a class="header-anchor" href="#basic-preparation" aria-label="Permalink to &quot;Basic Preparation&quot;">​</a></h1><h2 id="about-hig-glove" tabindex="-1">About HIG Glove <a class="header-anchor" href="#about-hig-glove" aria-label="Permalink to &quot;About HIG Glove&quot;">​</a></h2><h3 id="parts-name-and-usage" tabindex="-1">Parts Name and Usage <a class="header-anchor" href="#parts-name-and-usage" aria-label="Permalink to &quot;Parts Name and Usage&quot;">​</a></h3><p><img src="'+l+'" alt="AbouGlove"></p><table tabindex="0"><thead><tr><th style="text-align:center;">Number</th><th style="text-align:center;">Name</th><th style="text-align:center;">Usage</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;">Power Button &amp; LED</td><td style="text-align:center;"><strong>Press and Hold:</strong> Power ON / Power OFF</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">Menu Button &amp; LED</td><td style="text-align:center;"><strong>Press:</strong> Open / Close SteamVR Menu.<br> <strong>Press and Hold:</strong> Pairs with receiver. Power Button LED turns Purple.</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">Data Box</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">Hall Effect Module (Distal Joint)</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">Magnet Module (Middle Joint)</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:center;">Controller Module (Optional)</td><td style="text-align:center;">—</td></tr></tbody></table><h3 id="led-status-light" tabindex="-1">LED Status Light <a class="header-anchor" href="#led-status-light" aria-label="Permalink to &quot;LED Status Light&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:center;">Power Button LED</th><th style="text-align:center;">Meaning</th><th style="text-align:center;">Menu Button LED</th><th style="text-align:center;">Meaning</th></tr></thead><tbody><tr><td style="text-align:center;">Cyan</td><td style="text-align:center;">Receiver Not Connected</td><td style="text-align:center;">Blue</td><td style="text-align:center;">Charging Complete</td></tr><tr><td style="text-align:center;">Purple</td><td style="text-align:center;">Pairing With Receiver</td><td style="text-align:center;">Flashing Blue</td><td style="text-align:center;">Now Charging</td></tr><tr><td style="text-align:center;">Green</td><td style="text-align:center;">Battery: 100% - 40%</td><td style="text-align:center;">—</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">Orange</td><td style="text-align:center;">Battery: 40% - 20%</td><td style="text-align:center;">—</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">Red</td><td style="text-align:center;">Battery: 20% - 5%</td><td style="text-align:center;">—</td><td style="text-align:center;">—</td></tr><tr><td style="text-align:center;">Flashing Red</td><td style="text-align:center;">About to Shut Down</td><td style="text-align:center;">—</td><td style="text-align:center;">—</td></tr></tbody></table><h2 id="about-hig-gloveng" tabindex="-1">About HIG GloveNG <a class="header-anchor" href="#about-hig-gloveng" aria-label="Permalink to &quot;About HIG GloveNG&quot;">​</a></h2><h3 id="main-interface" tabindex="-1">Main Interface <a class="header-anchor" href="#main-interface" aria-label="Permalink to &quot;Main Interface&quot;">​</a></h3><p><img src="'+o+'" alt="AboutHIGNG"></p><table tabindex="0"><thead><tr><th style="text-align:center;">Number</th><th style="text-align:center;">Name</th><th style="text-align:center;">Usage</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;">Steam Icon</td><td style="text-align:center;">If light up: Means the HIG GloveNG has established communication with SteamVR.</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">Hand Icon</td><td style="text-align:center;">If light up: Means the HIG GloveNG has detected the Receiver.</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">Setting Button</td><td style="text-align:center;">Click to open HIG GloveNG Configuraions Window.</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">Play Button</td><td style="text-align:center;">Click to start glove tracking, click again to stop tracking.</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">Preset Change</td><td style="text-align:center;">Click to expand the HIG Glove&#39;s official Tuning presets, and select one to apply it.</td></tr></tbody></table><h3 id="configuraions-window" tabindex="-1">Configuraions Window <a class="header-anchor" href="#configuraions-window" aria-label="Permalink to &quot;Configuraions Window&quot;">​</a></h3><h4 id="controller-pose" tabindex="-1">Controller Pose <a class="header-anchor" href="#controller-pose" aria-label="Permalink to &quot;Controller Pose&quot;">​</a></h4><ul><li>Bind to current controllers：Select this option if you are using the handle as a glove position source.</li><li>Use selected devices：Select this option if auto-binding fails or if Tracker is used as the glove position source.</li></ul><h4 id="tuning" tabindex="-1">Tuning <a class="header-anchor" href="#tuning" aria-label="Permalink to &quot;Tuning&quot;">​</a></h4><ul><li>The Tuning function can be used if you wish to adjust the hand tuning data more finely for different usage scenarios or if your device&#39;s tuning presets are not included in HIG GloveNG for the time being.</li><li>The position of the glove is determined by six parameters. Taking Left hand as an example: the first line of parameters is used to adjust the Translation, the second line of parameters is used to adjust the Rotation.</li><li>You can click on the value and then enter the number directly for fine adjustment, or you can use the Up &amp; Down Keys to quickly adjust to a rough Transform.</li></ul><div class="info custom-block"><p class="custom-block-title">Info</p><p><code>Lighthouse Mode</code> is a historical artifact that was used to switch whether or not to recalculate the coordinate system, and will be removed in future releases.</p></div><h4 id="calibrating" tabindex="-1">Calibrating <a class="header-anchor" href="#calibrating" aria-label="Permalink to &quot;Calibrating&quot;">​</a></h4><ul><li>Calibrate Gestures:</li><li>Calibrate Joysticks:</li></ul><h4 id="vmc" tabindex="-1">VMC <a class="header-anchor" href="#vmc" aria-label="Permalink to &quot;VMC&quot;">​</a></h4><ul><li>Check <code>Enable VMC output</code> to send VMC data to the <code>Target port</code>.</li></ul><h4 id="curve-editor" tabindex="-1">Curve Editor <a class="header-anchor" href="#curve-editor" aria-label="Permalink to &quot;Curve Editor&quot;">​</a></h4><ul><li>Used to further adjust the HIG Glove effect to meet individual customization needs.</li></ul><h4 id="debugging" tabindex="-1">Debugging <a class="header-anchor" href="#debugging" aria-label="Permalink to &quot;Debugging&quot;">​</a></h4><ul><li>You can view and debug HIG Glove data here to assist in troubleshooting problem areas.</li></ul><h2 id="set-hig-glove-hardware" tabindex="-1">Set HIG Glove Hardware <a class="header-anchor" href="#set-hig-glove-hardware" aria-label="Permalink to &quot;Set HIG Glove Hardware&quot;">​</a></h2><h3 id="adjust-sensor-modules" tabindex="-1">Adjust Sensor Modules <a class="header-anchor" href="#adjust-sensor-modules" aria-label="Permalink to &quot;Adjust Sensor Modules&quot;">​</a></h3><p>Placement of Sensors (Hall Effect Module &amp; Magnet Module) Shall comply with:</p><ol><li><strong>Kinematic Clearance:</strong></li></ol><ul><li>Modules must maintain flush contact when fist is clenched <em>(no lift-off)</em></li><li>Modules shall avoid mutual collision when fingers are fully extended</li></ul><ol start="2"><li><strong>Proximity Optimization:</strong></li></ol><ul><li>Subject to Rule #1, minimize clearance between Hall Effect and Magnet modules</li><li>Maximize proximity while preventing mechanical interference</li></ul><p><img src="'+i+'" alt="AdjustSensor"></p><h3 id="mount-controller-tracker" tabindex="-1">Mount Controller/Tracker <a class="header-anchor" href="#mount-controller-tracker" aria-label="Permalink to &quot;Mount Controller/Tracker&quot;">​</a></h3><ul><li><p>Installation of Controller Holder</p><ol><li>Orient with <strong>arrow mark facing Type-C port</strong>, <strong>metal latch toward fingers</strong></li></ol><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+s+'" alt="1"></th><th style="text-align:center;"><img src="'+d+'" alt="2"></th></tr></thead></table><ol start="2"><li>Engage controller&#39;s <strong>side grip button</strong> into the clamp&#39;s bottom groove</li></ol><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+c+'" alt="3"></th><th style="text-align:center;"><img src="'+g+'" alt="4"></th></tr></thead></table></li><li><p>Installation of Tracker Holder</p></li></ul><table tabindex="0"><thead><tr><th style="text-align:center;">! WARNING !<br> For demo purposes only!</th><th style="text-align:center;">Do Not Remove quick-release base from Glove!</th></tr></thead><tbody><tr><td style="text-align:center;"><img src="'+u+'" alt="1"> 1. Slide quick-release module <strong>outward along arrow direction</strong></td><td style="text-align:center;"><img src="'+h+'" alt="2"> 2. <strong>Detach module completely</strong> from base</td></tr><tr><td style="text-align:center;"><img src="'+p+'" alt="3"> 3. <strong>Remove mounting screw</strong>, observe <strong>screw hole alignment</strong> and <strong>fool-proof pin orientation</strong></td><td style="text-align:center;"><img src="'+m+'" alt="4"> 4. Position quick-release module <strong>above Tracker base</strong> with proper alignment</td></tr><tr><td style="text-align:center;"><img src="'+y+'" alt="5"> 5. <strong>Tighten screw</strong> while pressing auxiliary handle until <strong>flush</strong></td><td style="text-align:center;"><img src="'+b+'" alt="6"> 6. Reinsert module into base until <strong>audible click</strong> is heard</td></tr><tr><td style="text-align:center;"><img src="'+x+'" alt="7"> 7. Installation complete</td><td style="text-align:center;"></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Tip</p><p>HIG Glove doesn&#39;t force the Controllers or Trackers mounting orientation, the official recommended mounting method is used in conjunction with the HIG GloveNG built-in Tuning presets.If there are special needs, you can also adjust the mounting direction of the position source to suit the situation and use the Tuning function to calibrate the HIG Glove&#39;s positioning coordinates.</p></div>',40)]))}const _=e(f,[["render",G]]);export{q as __pageData,_ as default};
