import{_ as a,c as i,a2 as e,o as t}from"./chunks/framework.B3cIRO-4.js";const c=JSON.parse('{"title":"软件介绍","description":"","frontmatter":{},"headers":[],"relativePath":"HIGM43-0/outline.md","filePath":"HIGM43-0/outline.md","lastUpdated":null}'),r={name:"HIGM43-0/outline.md"};function o(h,l,u,n,d,s){return t(),i("div",null,l[0]||(l[0]=[e('<h1 id="软件介绍" tabindex="-1">软件介绍 <a class="header-anchor" href="#软件介绍" aria-label="Permalink to &quot;软件介绍&quot;">​</a></h1><p>照搬 HIG Gunstock 后坐力模块文档</p><h1 id="硬件介绍" tabindex="-1">硬件介绍 <a class="header-anchor" href="#硬件介绍" aria-label="Permalink to &quot;硬件介绍&quot;">​</a></h1><h2 id="hig-m4-3-0" tabindex="-1">HIG M4 3.0 <a class="header-anchor" href="#hig-m4-3-0" aria-label="Permalink to &quot;HIG M4 3.0&quot;">​</a></h2><h3 id="护木模块" tabindex="-1">护木模块 <a class="header-anchor" href="#护木模块" aria-label="Permalink to &quot;护木模块&quot;">​</a></h3><p>拆卸方法：旋转并拔出 Type-C 连接</p><p>护木</p><ul><li>握住时模拟握持键</li></ul><p>摇杆</p><ul><li>全功能摇杆</li></ul><h3 id="m4-主体" tabindex="-1">M4 主体 <a class="header-anchor" href="#m4-主体" aria-label="Permalink to &quot;M4 主体&quot;">​</a></h3><p>电源键</p><ul><li>长按开机</li><li>再次长按关机</li></ul><p>配对键</p><ul><li>开机状态下，长按开始配对，指示灯变为青色</li><li>配对模式下，再次长按指示灯变为红色，不知道干什么用的</li><li>不知道干什么的模式下，再次长按恢复正常模式</li></ul><p>扳机</p><ul><li>对应VR手柄的扳机键，扣动来选择或开火</li></ul><p>握把</p><ul><li>对应</li></ul><p>按键模块</p><ul><li>摇杆</li><li>A 键</li><li>B 键</li><li>SteamVR 菜单键</li></ul><p>指示灯</p><ul><li>绿色：正常运行</li><li>青色：配对</li><li>红色：不知道什么意思</li></ul><p>装饰品：</p><ul><li>快慢机</li><li>弹匣</li><li>拉栓</li></ul><h3 id="电池-后托模块" tabindex="-1">电池/后托模块 <a class="header-anchor" href="#电池-后托模块" aria-label="Permalink to &quot;电池/后托模块&quot;">​</a></h3><p>拆卸方法：旋转并拔出 DC 口连接</p><h4 id="充电方法" tabindex="-1">充电方法 <a class="header-anchor" href="#充电方法" aria-label="Permalink to &quot;充电方法&quot;">​</a></h4><p>将充电器母口插入至电池模块公口</p><ul><li>此时充电器指示灯绿灯亮起：空载</li><li>将充电器插入插座，此时红灯亮起：正在充电</li><li>电器插入插座时，若绿灯亮起：电量已充满</li></ul><h4 id="调整后托距离" tabindex="-1">调整后托距离 <a class="header-anchor" href="#调整后托距离" aria-label="Permalink to &quot;调整后托距离&quot;">​</a></h4><p>按住后托、翘起 左右平移至合适的距离 松手即可完成调节</p><h2 id="hig-glove" tabindex="-1">HIG Glove <a class="header-anchor" href="#hig-glove" aria-label="Permalink to &quot;HIG Glove&quot;">​</a></h2><p>内容照搬手套文档</p><h1 id="使用方法" tabindex="-1">使用方法 <a class="header-anchor" href="#使用方法" aria-label="Permalink to &quot;使用方法&quot;">​</a></h1><h2 id="安装软件" tabindex="-1">安装软件 <a class="header-anchor" href="#安装软件" aria-label="Permalink to &quot;安装软件&quot;">​</a></h2><ul><li>纯英文路径</li><li>纯英文用户名</li></ul><h2 id="插入接收器" tabindex="-1">插入接收器 <a class="header-anchor" href="#插入接收器" aria-label="Permalink to &quot;插入接收器&quot;">​</a></h2><p>照搬 hig glove 的</p><ul><li>HIG Glove 接收器</li><li>HIG M4 3.0 接收器</li></ul><h2 id="打开软件" tabindex="-1">打开软件 <a class="header-anchor" href="#打开软件" aria-label="Permalink to &quot;打开软件&quot;">​</a></h2><ul><li>连接图标亮起</li><li>挥手图标亮起</li></ul><h2 id="调整硬件" tabindex="-1">调整硬件 <a class="header-anchor" href="#调整硬件" aria-label="Permalink to &quot;调整硬件&quot;">​</a></h2><p>HIG Glove：</p><ul><li>使用 HIG Glove 的夹具夹取 左手手柄</li></ul><p>HIG M4 3.0：</p><ul><li>向前推动固定卡扣并取下</li><li>利用夹具的形变特性避让后方的榫卯机构以打开夹具</li><li>将手柄的握持键对准夹具中的缺口放入并扣合夹具</li><li>装回固定卡扣，直到听见咔声</li></ul><h2 id="开机硬件" tabindex="-1">开机硬件 <a class="header-anchor" href="#开机硬件" aria-label="Permalink to &quot;开机硬件&quot;">​</a></h2><p>长按手套左侧按键开机 长按m4电源键开机</p><h2 id="启动-steamvr" tabindex="-1">启动 steamvr <a class="header-anchor" href="#启动-steamvr" aria-label="Permalink to &quot;启动 steamvr&quot;">​</a></h2><ul><li>戴上你的vr头显，随后通过 Steam 界面启动 SteamVR</li><li>如果一切正常： <ul><li>Steam 图标亮起</li><li>连接图标亮起</li><li>挥手图标亮起</li></ul></li></ul><h2 id="启动游戏" tabindex="-1">启动游戏 <a class="header-anchor" href="#启动游戏" aria-label="Permalink to &quot;启动游戏&quot;">​</a></h2><ul><li>通过steamvr启动受支持的游戏</li><li>在枪械软件里选择所使用的头显</li><li>单击右下角的播放键，自动应用数据，此时： <ul><li>左手位置变更为现实手部位置</li><li>右手位置变更为 M4 握把处位置</li></ul></li></ul><h2 id="按键对立" tabindex="-1">按键对立 <a class="header-anchor" href="#按键对立" aria-label="Permalink to &quot;按键对立&quot;">​</a></h2><p>左手：</p><ul><li>扳机键：扣动食指</li><li>握持键：中指或无名指 / 握住 M4 护木</li><li>摇杆：M4 护木摇杆</li><li>SteamVR 菜单键：按下 Type-C 接口右侧的黑色按钮</li></ul><p>右手：</p><ul><li>扳机键：扣动扳机</li><li>握持键：握住握把</li><li>A 键：按下印有标识 A 的按钮</li><li>B 键：按下印有标识 B 的按钮</li><li>摇杆：按键模块摇杆</li><li>SteamVR 菜单键：按下小黑色按钮</li></ul><h1 id="故障排查" tabindex="-1">故障排查 <a class="header-anchor" href="#故障排查" aria-label="Permalink to &quot;故障排查&quot;">​</a></h1><h2 id="灯亮不起来" tabindex="-1">灯亮不起来 <a class="header-anchor" href="#灯亮不起来" aria-label="Permalink to &quot;灯亮不起来&quot;">​</a></h2><p>照搬手套，改一下文件路径即可</p>',61)]))}const m=a(r,[["render",o]]);export{c as __pageData,m as default};
